import './App.css';
import React, {useState, useEffect} from "react";
import {v4} from 'uuid';
import {randomColor} from 'randomcolor'
import Draggable from "react-draggable";

function App() {
    const [item, setItem] = useState('')
    const [items, setItems] = useState(
        JSON.parse(localStorage.getItem('items')) || []
    )
    useEffect(() => {
        localStorage.setItem('items',JSON.stringify(items))
    } , [items])

    const newItem = () => {
        if (item.trim() !== '') {
            const newItem = {
                id: v4(),
                item,
                color:randomColor({
                    luminosity: 'light'
                }),
                defaultPos: {
                    x: 500,
                    y: -500
                }
            }
            setItems((items) => [...item, newItem])
            setItem('')
        } else {
            alert("Enter something ...")
            setItem('')
        }
    }

    const deleteNode = (id) => {

        setItems(items.filter((item) => item.id === id))
    }

  return (
    <div className="App">
     <div className='wrapper'>
       <input type="text" placeholder='Enter something...' onChange={(event) => setItem(event.target.value)}/>
       <button className='enter' onClick={ newItem}>ENTER</button>
     </div>

        {
            items.map((item, index) => {
                return (
                    <Draggable key={index} defaultPosition={item.defaultPos}>
                        <div className="todo__item" style={{backgroundColor: item.color}}>
                            {`${item.item}`}
                            <button className='delete' onClick={() => deleteNode(item.id)}>
                                X
                            </button>
                        </div>

                    </Draggable>
                )
            })
        }
    </div>
  );
}

export default App;
