import './App.css';
import React, {useState, useEffect} from "react";
import {v4} from 'uuid';
import {randomColor} from 'randomcolor'
import Draggable from "react-draggable";

function App() {
    const [item, setItem] = useState('')
    const [items, setItems] = useState(
        JSON.parse(localStorage.getItem('items')) || []
    )
    useEffect(() => {
        localStorage.setItem('items',JSON.stringify(items))
    } , [items])

    const newItem = () => {
        if (item.trim() !== '') {
            const newItem = {
                id: v4(),
                item,
                color:randomColor({
                    luminosity: 'light'
                }),
                defaultPosition: {
                    x: 500,
                    y: -500
                }
            }
            setItems((items) => [...item, newItem])
            setItem('')
        } else {
            alert("Enter something ...")
            setItem('')
        }
    }

  return (
    <div className="App">
     <div className='wrapper'>
       <input type="text" placeholder='Enter something...' onChange={(event) => setItem(event.target.value)}/>
       <button className='enter' onClick={ newItem}>ENTER</button>
     </div>

        {
            items.map((item, index) => {
                return (
                    <Draggable key={index} defaultPosition={item.defaultPosition}>
                        <div className="todo__item">
                            {`${item.item}`}
                            <button className='delete'>s
                                X
                            </button>
                        </div>

                    </Draggable>
                )
            })
        }
    </div>
  );
}

export default App;
